class AutomatonInstance {

    public void consume() {
        PortInstance inPort = null;
        loop: for (PortInstance port: self.getPort()) {
            if (port.getClassifier().iterator().next().directionForComponent(self.getClassifier().iterator().next()) == Direction.IN) {
                inPort = port;
                break loop;
            }
        }

        // consume the signal
        SignalInstance signal = inPort.getSignal().get(0);
        inPort.getSignal().remove(0);

        // find the triggered transition
        for(Transition transition: getTrigger()) {
            if (transition.getInput().getType().equals(signal.getClassifier().iterator().next())) {
                // create and send all output signals
                for (Output out: transition.getOutput()) {
                    PortInstance portInstance = null;
                    // find via port instance
                    for (PortInstance port: self.getPort()) {
                        if (port.getClassifier().iterator().next().equals(out.getVia())) {
                            portInstance = port;
                        }
                    }

                    // create and send output signal
                    SignalInstance outSignal = InstanceFactory.getFactory().createSignalInstance(out.getType());
                    portInstance.getSignal().add(outSignal);
                    portInstance.send();
                }
            }
        }
    }




    public ReflectiveCollection<? extends Transition> getTrigger() {
        ReflectiveCollection<Transition> result = new SetImpl<Transition>();
        for(Transition transition: self.getClassifier().iterator().next().getTransition()) {
            if (transition.getSource().contains(getActualState())) {
                result.add(transition);
            }
        }
        return result;
    }
}

class PortInstance {
    public void send() {
        SignalInstance signal = self.getSignal().get(0);
        self.getSignal().remove(0);
        PortInstance opposite = getOpposite();
        opposite.getSignal().add(signal);

        if (opposite.getComponent() instanceof AutomatonInstance) {
            ((AutomatonInstance)opposite.getComponent()).consume();
        }
    }

    public PortInstance getOpposite() {
        return self.getClassifier().iterator().next().getChannel().getTarget().iterator().next().getImplicitInstance();
    }
}

class Port {
    public Direction directionForComponent(Component component) {
        if (getDirection() == Direction.IN) {
            if (component instanceof System) {
                return Direction.IN;
            } else {
                return Direction.OUT;
            }
        } else {
            if (component instanceof System) {
                return Direction.OUT;
            } else {
                return Direction.IN;
            }
        }
    }
}
